postprocess_script:
let item;

let playlistMatch = /Finished downloading playlist: ([^
]+)/.exec(output.stdout);
if(playlistMatch) {
  item = playlistMatch[1];
}

if(!item) {
  let match = /Destination: ([^
]+)/.exec(output.stdout);
  if(match) {
    let splits = match[1].split('/');
    item = splits[splits.length - 1];
  }
}

if(!item) { return; }

return { ...output, description: { action: 'Downloaded', item } };
